xwikidoc.content:
{{include document='WiperDog.DefaultServletConstant'/}}
{{groovy}}
class GetServlet{
	def static params

	Object getParam(def name){
	    def defaultParams = DefaultServletConstant.DEFAULT_SERVLET
		if(params == null) {
			def xwiki_dir = System.getProperty('user.dir')
			def shell = new GroovyShell()
			File conf = new File(xwiki_dir + "/webapps/xwiki/resources/conf.params")
			if(conf.exists()){
				params = shell.evaluate(conf)
				if( params != null && params != []){
					defaultParams.each{ key,value ->
						def paramsKeys = params.keySet()
						if(!paramsKeys.contains(key)){
							params[key] = value
						}
					}
                                        return params[name]['servlet']
				} else {
					params = DefaultServletConstant.DEFAULT_SERVLET
				}
			} else {
			   params = DefaultServletConstant.DEFAULT_SERVLET
			} 
		}
		if( (name != null) && (name != "") ){
            return params[name]['servlet']
		}
		return null
	}
}{{/groovy}}
