xwikidoc.content:
{{groovy}}
class GetServlet{
 def static params

 Object getParam(def name){
     if(params == null) {
       def xwiki_dir = System.getProperty('user.dir')
       def shell = new GroovyShell()
       File f = new File(xwiki_dir +"/webapps/xwiki/resources/conf.params")
       params = shell.evaluate(f)
     }
    if( (name != null) && (name != "") ){
       if(params != null && !(params instanceof List) && params[name]!=null && params[name]!= ''){
          return params[name]
       }
    } else {
       return null
    }
 }
}
{{/groovy}}
