var logFileName

require(['jquery'],function($){

    // Set width for area content
    var widthMenu = document.getElementById('xwikicontent').offsetWidth * 0.2
    var widthContent = document.getElementById('xwikicontent').offsetWidth * 0.7
    $("#lstLog").css('width', widthMenu)
    $("#contentLog").css('width', widthContent)

    // Get servlet
    var param = 'logfileinfo'
    var servlet = ''
    $.ajax({
        type: "GET",
        url:"/xwiki/bin/get/WiperDog/GetServlet?outputSyntax=plain&name=" + param,
        success : function(result){
            processData(result)
        },
        error: function(xhr,status,error){
            $("#message_add").text("Can not get data info from configuration file !.Wiperdog is on yet ?")
        }
    });

    // Draw list log and get anchor text
    function drawListLog(domainURL,result){
        // Draw
        var htmlSource = "<ul>"
        var lstAID = new Array()
        for(var i = 0; i < result.length; i ++) {
            var aID = "logfile" + i
            lstAID[i] = aID
            htmlSource += "<li><a id=\"" + aID + "\">" + result[i] + "</a></li>"
        }
        htmlSource += "</ul>"
        $("#lstLog").html(htmlSource)

        // Get anchor text
         $("#lstLog a").click(function(){
             logFileName = $(this).text()
             var aID = $(this).attr('id')
             for(var e = 0; e < lstAID.length; e ++) {
                 if(lstAID[e] == aID) {
                     $("#" + lstAID[e]).css('color', '#1E90FF')
                 } else {
                     $("#" + lstAID[e]).css('color', 'black')
                 }
             }

	     ajax_post_data(domainURL,function(result){})
         });
    }

    // Set data response to textarea
    function setDataForTextArea(result){
        var htmlSource = ""
        for(var i = 0; i < result.length; i ++) {
            htmlSource += result[i] + "<br/>"
        }
        $("#contentLog").html(htmlSource)
    }

    // Get list log
    function processData(domainURL) {
        ajax_get_data(domainURL,function(result){});
    }

    function ajax_get_data(domainURL,callback){
	$.ajax({
	    type: "GET",
	    crossDomain: true,
	    url: domainURL,
	    dataType: 'json',
	    success : function(result){
	        drawListLog(domainURL,result)
	        callback(result)
	    },
	    error : function(xhr,status,error){
                $("#message_add").text("Can not get data info from configuration file !.Wiperdog is on yet ?")
	    }
    	});
    }

    // Get content of log
    function ajax_post_data(domainURL,callback){
        var logNameMap = {}
        logNameMap['log'] = logFileName
        $.ajax({
	    type: "POST",
	    crossDomain: true,
	    url: domainURL,
	    dataType: 'json',
            data: JSON.stringify(logNameMap),
	    success : function(result){
	        setDataForTextArea(result)
	        callback(result)
            },
	    error : function(xhr,status,error){
                $("#message_add").text("Can not get data info from configuration file !.Wiperdog is on yet ?")
	    }
        });
    }

jQuery.noConflict();
});

