function ajaxGetDataPolicy(data) {
    $.ajax({
        type: "POST",
        crossDomain: true,
        url: "/xwiki/bin/get/Lib/MsgPolicyFromMongo?outputSyntax=plain",
        dataType: 'text',
        data: data,
        success: function(result) {
            console.log("---------------- " + result)
        },
        error: function(xhr, status, error) {
            alert("There is an error! Wiperdog agent is on yet?")
            console.log(error)
            console.log(status)
            console.log(xhr)
        }
    });    
}

jQuery.noConflict()
});

