     var update = false
     for(key_res in result ){
	     if(key === key_res){
		   update = true
		   break
		 }
	 }
	if(update){
	   var confirm_update = confirm("Name exists ,want to update ?")
	   if(confirm_update){
		   delete result[key]
		   var rows = document.getElementById('table_display').rows
		   for(var i = 0 ; i < rows.length ; i++) {
			   var key2remove = rows[i].cells[0].innerText
