					rows[i].cells[1].innerText = $("#host").val()
					rows[i].cells[2].innerText = $("#port").val()
					rows[i].cells[3].innerText = $("#servlet").val()
                                        rows[i].cells[4].innerText = $("#desc").val()
					$("#message").text('Update succefully !')
