   port = arr_str[2].substring(0,arr_str[2].indexOf("/"))
   servletPath = arr_str[2].substring(arr_str[2].indexOf("/")+1)
  } else {
    hostname = DefaultServletConstant.HOST
    port = DefaultServletConstant.PORT
    if(key == "dbinfo"){
       servletPath = DefaultServletConstant.DBINFO_SERVLET
    } else if(key == "dbcommon"){
       servletPath = DefaultServletConstant.DBCOMMON_SERVLET
    } else if(key == "jobdoc"){
       servletPath = DefaultServletConstant.JOBDOC_SERVLET
    } else if(key == "console"){
       servletPath = DefaultServletConstant.CONSOLE_SERVLET
    } else {
       servletPath = "Not Available "
    }
  }
  if(value.desc != [] && value.desc != null){
       description = value.desc
    } else {
       description = "No description"
    }
   println "<tr><td>$key</td><td>$hostname</td><td>$port</td><td>$servletPath</td><td>$description</td><td style='width:120px,text-align:center'><input type='button'               style='width:50px' value='Delete' class='delete button'/><input type='button' value='Edit' style='width:50px' class='edit button'/></td></tr>" 
}
